const App = () => {
  const [name, setName] = useState("");

  // ComponenteDidUpdate
  useEffect(() => {
    console.log("haciendo lo mismo que con ComponentDidUpdate")
  });

    return(
      <div>
        <h1>
          Hola
        </h1>
        <input value={name} onChange={({target : {value}}) => setName(value)}></input>
      </div>
    );
}

export default App;
